import React, { useState } from 'react'
import './App.css'

function App() {

  const ques=[
    {
      questiontext: "What is the capital of India?" ,
      options:[
          "New Delhi",
          "Hyderabad",
          "Banglore",
          "Chennai"
      ]
    },
    {
      questiontext: "Who is the PM of India?" ,
      options:[
        "Yogi Adityanadh",
        "Narendra Modi",
        "Amit Shah",
        "Rahul Gandhi"
      ]
    },
    {
      questiontext: "Who is president of India?",
      options:[
        "Sonia Gandhi",
        "Narendra Modi",
        "Pawan kalyan",
        "Draupadi Murmu"
      ]
    },
    {
      questiontext: "What is the capital of Andhra Pradesh ?" ,
      options:[
        "Warangal",
        "Hyderabad",
        "Amrawati",
        "None of the Above"
      ]
    }
  ]

