import React, { useState } from 'react'
import './App.css'

function App() {

  const ques=[
    {
      questiontext: "What is the capital of India?" ,
      options:[
          "New Delhi",
          "Hyderabad",
          "Banglore",
          "Chennai"
      ]
    },
    {
      questiontext: "Who is the PM of India?" ,
      options:[
        "Yogi Adityanadh",
        "Narendra Modi",
        "Amit Shah",
        "Rahul Gandhi"
      ]
    },
    {
      questiontext: "Who is president of India?",
      options:[
        "Sonia Gandhi",
        "Narendra Modi",
        "Pawan kalyan",
        "Draupadi Murmu"
      ]
    },
    {
      questiontext: "What is the capital of Andhra Pradesh ?" ,
      options:[
        "Warangal",
        "Hyderabad",
        "Amrawati",
        "None of the Above"
      ]
    }
  ]

  const [count,setCount]= useState(0)
  const [msg,setmsg]= useState('')

  function PreQue(){
    if(count>0){
    setCount(count-1)
    }
  }

  function NextQue(){
    if(count==ques.length){
      setmsg('Quiz Completed')
    if(count<ques.length-1){
        setCount(count+1)    
    }
  }
        
  }
  


  return (
    <div className="container">
      <h1>My Quiz Application</h1>
        <div className="card">
            <h2>Question No: {count+1} / 4</h2>
            <h3>{ques[count].questiontext}</h3>

            <div id="option">
              {ques[count].options.map((x,index) => <li key={index}>{x}</li>)}
            </div>
            <p>{msg}</p>
              <br>
              </br>
            <div className="btn">
              {count==ques.length && setmsg('')}
             {count>0 && <button id='preq' onClick={PreQue}>Previous Question</button>}
              <button onClick={NextQue} disabled={count==ques.length-1}>Next Question</button>
            </div>
        </div>
    </div>
  )
}

export default App
